export default function capitalizeString(input) {
  // Check whether the input is a string
  if (typeof input !== 'string') {
    throw Error('Please provide only strings as input');
  }
  return input
    .split(' ')
    .map(
      el =>
        el.length > 1
          ? el[0].toUpperCase() + el.slice(-el.length + 1)
          : el[0].toUpperCase()
    )
    .join(' ');
}
