import capitalizeString from './index';

describe('capitalizeString', () => {
  it('It should capitalize every first letter in a sentence', () => {
    expect(capitalizeString("i'm a little tea pot")).toBe(
      "I'm A Little Tea Pot"
    );
    expect(capitalizeString('hERE iS mY hANDLE hERE iS mY SPOUT')).toBe(
      'HERE IS MY HANDLE HERE IS MY SPOUT'
    );
    expect(capitalizeString('valentino')).toBe('Valentino');
    expect(capitalizeString('caty')).toBe('Caty');
    expect(capitalizeString('a')).toBe('A');
  });
});
